import React, { useState } from "react";

const Click = () => {
  const [showImage1, setShowImage1] = useState(false);
  const [showParagraph1, setShowParagraph1] = useState(false);
  const [showImage2, setShowImage2] = useState(false);
  const [showParagraph2, setShowParagraph2] = useState(false);

  return (
    <div style={{
        display: "flex",
        justifyContent: "center",
        gap: "50px",
        flexWrap: "wrap",}}>
          
      <div style={{
          width: "220px",
          height: "380px",
          border: "2px solid black",
          display: "flex",
          flexDirection: "column",
          justifyContent: "space-around",
          alignItems: "center",
          padding: "10px",
          backgroundColor: "#d1f0d1",}}>

        <h3>SCIENTIST</h3>
        <div style={{
            width: "180px",
            height: "150px",
            border: "1px solid black",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            cursor: "pointer",}}

          onClick={() => setShowImage1(true)}>
          {showImage1 ? (
            <img
              src="data:image/jpeg;base64,"
              alt="SCIENTIST" style={{ width: "100%", height: "100%", objectFit: "cover" }}/>
          ) : ("Click Me")}</div>

        <div style={{
            width: "180px",
            height: "120px",
            border: "1px solid black",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            cursor: "pointer",
            textAlign: "center",
            padding: "5px",}}
          onDoubleClick={() => setShowParagraph1(true)}>

          {showParagraph1 ? (
            <p>Nikola Tesla (10 July 1856 â€“ 7 January 1943) was a Serbian-American engineer, futurist, and inventor.</p>
          ) : ("Double Click Me")}</div></div>

      <div style={{
          width: "220px",
          height: "380px",
          border: "2px solid black",
          display: "flex",
          flexDirection: "column",
          justifyContent: "space-around",
          alignItems: "center",
          padding: "10px",
          backgroundColor: "#f0d1d1",}}>

        <h3>DRDO</h3>
        <div style={{
            width: "180px",
            height: "150px",
            border: "1px solid black",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            cursor: "pointer",}}

          onMouseEnter={() => setShowImage2(true)}>
          {showImage2 ? (
            <img
              src="data:image/png;base64,"
              alt="DRDO" style={{ width: "100%", height: "100%", objectFit: "cover" }}/>
          ) : ("Hover Me")}</div>

        <div style={{
            width: "180px",
            height: "120px",
            border: "1px solid black",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            cursor: "pointer",
            textAlign: "center",
            padding: "5px",}}

          onContextMenu={(e) => {
            e.preventDefault();
            setShowParagraph2(true);}}>

          {showParagraph2 ? (
            <p>The Defence Research and Development Organisation (DRDO) is an agency under the Department of Defence Research and Development in the Ministry of Defence of the Government of India</p>
          ) : ("Right Click Me")}
          </div></div></div>);};

export default Click;
